var validator = require('validator');

function user(client){
    return {
        addUser: function(user, callback){
            if(!user.email || validator.isEmail(user.email)) return callback(new Error('Please provide a valid email'));
            if(!user.password) return callback(new Error('Please provide a password'));

            var token = generateToken(user);
            callback(null, {statusCode: 200, data: token});
        }
    }
}

module.exports = user;