var placesQueries = {
    all: function(q){
        return {
            index: 'places',
            body: {
                query: {
                    filtered: {
                        filter: {
                            geo_distance: {
                                distance: (q.distance / 1000) + 'km',
                                location: [+q.lat, +q.lon]
                            }
                        },
                        query: {
                            bool : {
                                must : {
                                    terms : { "tags" : q.tags, minimum_should_match : 1}
                                }
                            }
                        }
                    }
                }
            }
        }
    }
};

module.exports = placesQueries;

